import React, { useEffect, useState } from 'react';
import './slideShow.css';

export default function SlideShow() {
    const slideshowDuration = 4000;
    const [currentIndex, setCurrentIndex] = useState(0);

    const slidesData = [
        {
            title: 'Slide title 1',
            description: 'Slide description 1',
            imageUrl: 'https://www.alixbdanthenay.fr/wp-content/uploads/2015/07/Indispensable-1.jpg',
        },
        {
            title: 'Slide title 2',
            description: 'Slide description 2',
            imageUrl: 'https://www.alixbdanthenay.fr/wp-content/uploads/2015/07/Indispensable-4-1.jpg',
        },
        {
            title: 'Slide title 3',
            description: 'Slide description 3',
            imageUrl: 'https://www.alixbdanthenay.fr/wp-content/uploads/2016/11/11.jpg',
        },
        {
            title: 'Slide title 4',
            description: 'Slide description 4',
            imageUrl: 'https://www.alixbdanthenay.fr/wp-content/uploads/2016/11/20mars17-sans-typo.jpg',
        },
    ];

    useEffect(() => {
        const timeout = setTimeout(() => {
            slideshowNext();
        }, slideshowDuration);

        return () => {
            clearTimeout(timeout);
        };
    }, [currentIndex, slideshowNext]);

    const slideshowSwitch = (index) => {
        setCurrentIndex(index);
    };

    const slideshowNext = () => {
        const nextIndex = (currentIndex + 1) % slidesData.length;
        slideshowSwitch(nextIndex);
    };

    return (
        <main className="main-content">
            <section className="slideshow">
                <div className="slideshow-inner">
                    <div className="slides">
                        {slidesData.map((slide, index) => (
                            <div
                                key={index}
                                className={`slide ${index === currentIndex ? 'is-active' : ''}`}
                            >
                                <div className="slide-content">
                                    <div className="caption">
                                        <div className="title">{slide.title}</div>
                                        <div className="text">
                                            <p>{slide.description}</p>
                                        </div>
                                        <a href="#" className="btn">
                                            <span className="btn-inner">Learn More</span>
                                        </a>
                                    </div>
                                </div>
                                <div className="image-container">
                                    <img
                                        src={slide.imageUrl}
                                        alt=""
                                        className="image"
                                    />
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className="pagination">
                        {slidesData.map((_, index) => (
                            <div
                                key={index}
                                className={`item ${index === currentIndex ? 'is-active' : ''}`}
                                onClick={() => slideshowSwitch(index)}
                            >
                                <span className="icon">{index + 1}</span>
                            </div>
                        ))}
                    </div>
                    <div className="arrows">
                        <div className="arrow prev" onClick={() => slideshowNext()}>
              <span className="svg svg-arrow-left">
                {/* Your SVG code for the left arrow */}
              </span>
                        </div>
                        <div className="arrow next" onClick={() => slideshowNext()}>
              <span className="svg svg-arrow-right">
                {/* Your SVG code for the right arrow */}
              </span>
                        </div>
                    </div>
                </div>
            </section>
        </main>
    );
}
