import "./slideShow.css"
export default function SlideShow(){
    var slideIndex = 0;
    showSlides();

    function showSlides() {
        var i;
        var slides = document.getElementsByClassName("mySlides");
        var dots = document.getElementsByClassName("dot");
        for (i = 0; i < slides.length; i++) {
            slides[i].style.display = "none";
        }
        slideIndex++;
        if (slideIndex> slides.length) {slideIndex = 1}
        for (i = 0; i < dots.length; i++) {
            dots[i].className = dots[i].className.replace(" active", "");
        }
        slides[slideIndex-1].style.display = "block";
        dots[slideIndex-1].className += " active";
        setTimeout(showSlides, 2000); // Chuyển ảnh sau 2 giây
    }

    return (
        <div>
            <h1>LIST HOUSE FOR YOU</h1>
            <div className="slideshow-container">
                <div className="mySlides fade">
                    <div className="numbertext">1 / 4</div>
                    <img src="https://images.pexels.com/photos/3554424/pexels-photo-3554424.jpeg?auto=compress&cs=tinysrgb&w=600" style={{width: '100%', height:'750px'}} />
                    <div className="text">Chú thích cho ảnh 1</div>
                </div>
                <div className="mySlides fade">
                    <div className="numbertext">2 / 4</div>
                    <img src="https://images.pexels.com/photos/821357/pexels-photo-821357.jpeg?auto=compress&cs=tinysrgb&w=600" style={{width: '100%', height:'750px'}} />
                    <div className="text">Chú thích cho ảnh 2</div>
                </div>
                <div className="mySlides fade">
                    <div className="numbertext">3 / 4</div>
                    <img src="https://images.pexels.com/photos/2082087/pexels-photo-2082087.jpeg?auto=compress&cs=tinysrgb&w=600" style={{width: '100%', height:'750px'}} />
                    <div className="text">Chú thích cho ảnh 3</div>
                </div>
                <div className="mySlides fade">
                    <div className="numbertext">4 / 4</div>
                    <img src="https://images.pexels.com/photos/1879061/pexels-photo-1879061.jpeg?auto=compress&cs=tinysrgb&w=600" style={{width: '100%',height:'750px'}} />
                    <div className="text">Chú thích cho ảnh 4</div>
                </div>
            </div>
            <br />
            <div style={{textAlign: 'center'}}>
                <span className="dot" />
                <span className="dot" />
                <span className="dot" />
                <span className="dot" />
            </div>
            <h1>2</h1>
            <h1>2</h1>
            <h1>2</h1>
            <h1>2</h1>
            <h1>2</h1>
            <h1>2</h1>
            <h1>2</h1>
            <h1>2</h1>
        </div>



    );
}
