import React, { useEffect, useState } from 'react';
import './slideShow.css';

export default function SlideShow() {
    const slideshowDuration = 4000;
    const [currentIndex, setCurrentIndex] = useState(0);

    const slidesData = [
        {
            title: 'Slide title 1',
            description: 'Slide description 1',
            imageUrl: 'https://www.alixbdanthenay.fr/wp-content/uploads/2015/07/Indispensable-1.jpg',
        },
        {
            title: 'Slide title 2',
            description: 'Slide description 2',
            imageUrl: 'https://www.alixbdanthenay.fr/wp-content/uploads/2015/07/Indispensable-4-1.jpg',
        },
        {
            title: 'Slide title 3',
            description: 'Slide description 3',
            imageUrl: 'https://www.alixbdanthenay.fr/wp-content/uploads/2016/11/11.jpg',
        },
        {
            title: 'Slide title 4',
            description: 'Slide description 4',
            imageUrl: 'https://www.alixbdanthenay.fr/wp-content/uploads/2016/11/20mars17-sans-typo.jpg',
        },
    ];

    useEffect(() => {
        const timeout = setTimeout(() => {
            slideshowNext();
        }, slideshowDuration);

        return () => {
            clearTimeout(timeout);
        };
    }, [currentIndex]);

    const slideshowSwitch = (index) => {
        setCurrentIndex(index);
    };

    const slideshowNext = () => {
        const nextIndex = (currentIndex + 1) % slidesData.length;
        slideshowSwitch(nextIndex);
    };

    return (
        <section className="cd-hero">
            <ul className="cd-hero-slider">
                <li className="selected">
                    <div className="cd-full-width">
                        <h2>Hero slider</h2>
                        <p>A simple, responsive slideshow in CSS &amp; jQuery.</p>
                        <a href="https://codyhouse.co/?p=675" className="cd-btn">Article &amp; Download</a>
                    </div> {/* .cd-full-width */}
                </li>
                <li>
                    <div className="cd-half-width">
                        <h2>Slide title here</h2>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. In consequatur cumque natus!</p>
                        <a href="#0" className="cd-btn">Start</a>
                        <a href="#0" className="cd-btn secondary">Learn More</a>
                    </div> {/* .cd-half-width */}
                    <div className="cd-half-width cd-img-container">
                        <img src="https://codyhouse.co/demo/hero-slider/assets/tech-1.jpg" alt="tech 1" />
                    </div> {/* .cd-half-width.cd-img-container */}
                </li>
                <li>
                    <div className="cd-half-width cd-img-container">
                        <img src="https://codyhouse.co/demo/hero-slider/assets/tech-2.jpg" alt="tech 2" />
                    </div> {/* .cd-half-width.cd-img-container */}
                    <div className="cd-half-width">
                        <h2>Slide title here</h2>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. In consequatur cumque natus!</p>
                        <a href="#0" className="cd-btn">Start</a>
                        <a href="#0" className="cd-btn secondary">Learn More</a>
                    </div> {/* .cd-half-width */}
                </li>
                <li className="cd-bg-video">
                    <div className="cd-full-width">
                        <h2>Slide title here</h2>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Animi, explicabo.</p>
                        <a href="#0" className="cd-btn">Learn more</a>
                    </div> {/* .cd-full-width */}
                    {/*<div class="cd-bg-video-wrapper" data-video="https://codyhouse.co/demo/hero-slider/assets/video/video">*/}
                    <div className="cd-bg-video-wrapper" data-video="https://avvimeo-a.akamaihd.net/09682/750/74745479">
                        {/* video element will be loaded using jQuery */}
                    </div> {/* .cd-bg-video-wrapper */}
                </li>
                <li>
                    <div className="cd-full-width" style={{background: 'url(https://i.vimeocdn.com/video/222142554.webp?mw=1280&mh=720) no-repeat 50% 50%', backgroundSize: 'cover'}}>
                        {/*<h2>PROFESSIONal</h2>*/}
                        <p className="professional">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Animi, explicabo.</p>
                        <a href="#0" className="cd-btn">Start</a>
                        <a href="#0" className="cd-btn secondary">Learn More</a>
                    </div> {/* .cd-full-width */}
                </li>
                <li>
                    <div className="cd-full-width">
                        {/* .begin 3D STUFF HERE */}
                        <div className="cd-background-wrapper">
                            <figure className="cd-floating-background is-absolute">
                                <img src="https://i.vimeocdn.com/video/476215411.webp?mw=1280&mh=720" alt="image-1" />
                            </figure>
                        </div>
                        {/* .end 3D STUFF HERE */}
                    </div> {/* .cd-full-width */}
                </li>
            </ul> {/* .cd-hero-slider */}
            <div className="cd-slider-nav">
                <nav>
                    <span className="cd-marker item-1" />
                    <ul>
                        <li className="selected"><a href="#0">Intro</a></li>
                        <li><a href="#0">Tech 1</a></li>
                        <li><a href="#0">Tech 2</a></li>
                        <li><a href="#0">Video</a></li>
                        <li><a href="#0">Image</a></li>
                        <li><a href="#0">Image 2</a></li>
                    </ul>
                </nav>
            </div> 
        </section>
    );
}
